public class EvenOddSum {

    public static void main(String[] args) {
        int start = 1;
        int end = 100;

        Thread evenThread = new Thread(new SumEven(start, end));
        Thread oddThread = new Thread(new SumOdd(start, end));

        evenThread.start();
        oddThread.start();

        try {
            evenThread.join();
            oddThread.join();
        } catch (InterruptedException e) {
            e.printStackTrace();
        }

        System.out.println("Sum of even numbers: " + SumEven.getSum());
        System.out.println("Sum of odd numbers: " + SumOdd.getSum());
    }
}

class SumEven implements Runnable {
    private int start;
    private int end;
    private static long sum;

    public SumEven(int start, int end) {
        this.start = start;
        this.end = end;
    }

    @Override
    public void run() {
        sum = 0;
        for (int i = start; i <= end; i++) {
            if (i % 2 == 0) {
                sum += i;
            }
        }
    }

    public static long getSum() {
        return sum;
    }
}

class SumOdd implements Runnable {
    private int start;
    private int end;
    private static long sum;

    public SumOdd(int start, int end) {
        this.start = start;
        this.end = end;
    }

    @Override
    public void run() {
        sum = 0;
        for (int i = start; i <= end; i++) {
            if (i % 2 != 0) {
                sum += i;
            }
        }
    }

    public static long getSum() {
        return sum;
    }
}

