// Function to compare two files by size.
int compare(const void *a, const void *b) {
    struct stat st_a, st_b;
    stat(*(const char **)a, &st_a);
    stat(*(const char **)b, &st_b);

    if (st_a.st_size > st_b.st_size)
        return 1;
    else if (st_a.st_size < st_b.st_size)
        return -1;
    else
        return 0;
}

int main(int argc, char **argv) {
    DIR *dir;
    struct dirent *entry;
    int file_count = 0;
    char **file_list;

    if (argc != 2) {
        printf("Usage: %s directory\n", argv[0]);
        return 1;
    }

    dir = opendir(argv[1]);

    if (!dir) {
        perror("opendir");
        return 1;
    }

    // Count the number of files in the directory.
    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_REG)
            file_count++;
    }
    rewinddir(dir);

    // Allocate memory for the file list and populate it.
    file_list = malloc(file_count * sizeof(char *));
    int i = 0;
    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_REG) {
            file_list[i] = malloc(strlen(argv[1]) + strlen(entry->d_name) + 2);
            sprintf(file_list[i], "%s/%s", argv[1], entry->d_name);
            i++;
        }
    }
    closedir(dir);

    // Sort the file list by size.
    qsort(file_list, file_count, sizeof(char *), compare);

    // Print the sorted file list.
    for (i = 0; i < file_count; i++) {
        printf("%s\n", file_list[i]);
        free(file_list[i]);
    }
    free(file_list);

    return 0;
}
