    // Count the number of files in the directory.
    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_REG)
            file_count++;
    }
    rewinddir(dir);

    // Allocate memory for the file list and populate it.
    file_list = malloc(file_count * sizeof(char *));
    int i = 0;
    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_REG) {
            file_list[i] = malloc(strlen(argv[1]) + strlen(entry->d_name) + 2);
            sprintf(file_list[i], "%s/%s", argv[1], entry->d_name);
            i++;
        }
    }
    closedir(dir);

    // Sort the file list by size.
    qsort(file_list, file_count, sizeof(char *), compare);

    // Print the sorted file list.
    for (i = 0; i < file_count; i++) {
        printf("%s\n", file_list[i]);
        free(file_list[i]);
    }
    free(file_list);

    return 0;
}
